import { Component, OnInit, OnDestroy } from '@angular/core';
import { Student } from '../models/student.model';
import { StudentService } from '../services/student.service';
import { interval, Subscription } from 'rxjs';

@Component({
  selector: 'app-student-list',
  templateUrl: './student-list.component.html',
  styleUrls: ['./student-list.component.css']
})
export class StudentListComponent implements OnInit, OnDestroy {
  students: Student[] = [];
  filteredStudents: Student[] = [];
  searchId: string = '';
  isLoading: boolean = false;
  error: string = '';
  
  // Otomatik yenileme için
  private refreshInterval: Subscription | null = null;
  countdown: number = 60; // 60 saniye
  private countdownInterval: Subscription | null = null;
  lastRefreshTime: Date = new Date();

  constructor(private studentService: StudentService) { }

  ngOnInit(): void {
    this.loadStudents();
    this.startAutoRefresh();
  }

  ngOnDestroy(): void {
    this.stopAutoRefresh();
  }

  loadStudents(): void {
    this.isLoading = true;
    this.error = '';
    
    console.log('Öğrenci verileri yükleniyor...');
    
    this.studentService.getStudents().subscribe({
      next: (data) => {
        console.log('API\'den gelen veri:', data);
        this.students = data;
        this.filteredStudents = data;
        this.isLoading = false;
        this.lastRefreshTime = new Date();
        console.log('Öğrenci sayısı:', data.length);
      },
      error: (error) => {
        console.error('API Hatası detayı:', error);
        this.error = 'Öğrenci verileri yüklenirken hata oluştu: ' + error.message;
        this.isLoading = false;
        
        // CORS hatası kontrolü
        if (error.message.includes('CORS') || error.status === 0) {
          this.error = 'CORS hatası: API sunucusu CORS ayarlarını kontrol edin. API\'nin çalıştığından emin olun.';
        }
      }
    });
  }

  onSearch(): void {
    if (!this.searchId.trim()) {
      this.filteredStudents = this.students;
      return;
    }

    const searchIdNum = parseInt(this.searchId.trim());
    if (isNaN(searchIdNum)) {
      this.error = 'Lütfen geçerli bir öğrenci ID girin';
      return;
    }

    this.filteredStudents = this.students.filter(student => 
      student.id === searchIdNum
    );

    if (this.filteredStudents.length === 0) {
      this.error = 'Belirtilen ID ile öğrenci bulunamadı';
    } else {
      this.error = '';
    }
  }

  clearSearch(): void {
    this.searchId = '';
    this.filteredStudents = this.students;
    this.error = '';
  }

  // Otomatik yenileme metodları
  startAutoRefresh(): void {
    // Her 60 saniyede bir veri yenile
    this.refreshInterval = interval(60000).subscribe(() => {
      this.loadStudents();
    });

    // Geri sayım sayacı
    this.countdownInterval = interval(1000).subscribe(() => {
      this.countdown--;
      if (this.countdown <= 0) {
        this.countdown = 60;
      }
    });
  }

  stopAutoRefresh(): void {
    if (this.refreshInterval) {
      this.refreshInterval.unsubscribe();
    }
    if (this.countdownInterval) {
      this.countdownInterval.unsubscribe();
    }
  }

  // Manuel yenileme
  refreshData(): void {
    this.loadStudents();
    this.countdown = 60;
    this.lastRefreshTime = new Date();
  }
}
<div class="container">
  <div class="header">
    <h1>Öğrenci Listesi</h1>
    <div class="search-section">
      <div class="search-box">
        <input 
          type="text" 
          [(ngModel)]="searchId" 
          placeholder="Öğrenci ID girin..."
          class="search-input"
          (keyup.enter)="onSearch()"
        >
        <button (click)="onSearch()" class="search-btn">
          <i class="search-icon">🔍</i>
          Ara
        </button>
        <button (click)="clearSearch()" class="clear-btn">
          Temizle
        </button>
        <button (click)="refreshData()" class="refresh-btn">
          <i class="refresh-icon">🔄</i>
          Yenile
        </button>
      </div>
    </div>
  </div>

  <!-- Otomatik Yenileme Bilgisi -->
  <div class="refresh-info">
    <div class="refresh-status">
      <span class="refresh-label">Son Yenileme:</span>
      <span class="refresh-time">{{ lastRefreshTime | date:'HH:mm:ss' }}</span>
    </div>
    <div class="countdown-section">
      <span class="countdown-label">Sonraki Yenileme:</span>
      <span class="countdown-timer" [class.warning]="countdown <= 10">
        {{ countdown }}s
      </span>
    </div>
  </div>

  <div class="error-message" *ngIf="error">
    {{ error }}
  </div>

  <div class="loading" *ngIf="isLoading">
    <div class="spinner"></div>
    <p>Öğrenci verileri yükleniyor...</p>
  </div>

  <div class="table-container" *ngIf="!isLoading">
    <table class="student-table">
      <thead>
        <tr>
          <th>ID</th>
          <th>Öğrenci No</th>
          <th>Ad</th>
          <th>Soyad</th>
          <th>E-posta</th>
          <th>Doğum Tarihi</th>
          <th>Kayıt Tarihi</th>
          <th>Durum</th>
        </tr>
      </thead>
      <tbody>
        <tr *ngFor="let student of filteredStudents" class="student-row">
          <td>{{ student.id }}</td>
          <td>{{ student.studentNumber }}</td>
          <td>{{ student.firstName }}</td>
          <td>{{ student.lastName }}</td>
          <td>{{ student.email }}</td>
          <td>{{ student.birthDate | date:'dd.MM.yyyy' }}</td>
          <td>{{ student.enrollmentDate | date:'dd.MM.yyyy' }}</td>
          <td>
            <span [class]="student.isActive ? 'status-active' : 'status-inactive'">
              {{ student.isActive ? 'Aktif' : 'Pasif' }}
            </span>
          </td>
        </tr>
      </tbody>
    </table>

    <div class="no-data" *ngIf="filteredStudents.length === 0 && !error">
      <p>Gösterilecek öğrenci bulunamadı.</p>
    </div>
  </div>

  <div class="info-section">
    <p class="total-count">
      Toplam {{ filteredStudents.length }} öğrenci gösteriliyor
    </p>
  </div>
</div>
