import { Component, OnInit, OnDestroy } from '@angular/core';

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css']
})
export class AppComponent implements OnInit, OnDestroy {
  title = 'Yönetim Sistemi';
  activeTab: 'dashboard' | 'students' | 'materials' = 'dashboard';
  isDarkMode = false;
  isOnline = navigator.onLine;

  ngOnInit(): void {
    // Load saved theme preference
    const savedTheme = localStorage.getItem('theme');
    this.isDarkMode = savedTheme === 'dark';

    // Listen for online/offline events
    window.addEventListener('online', () => this.isOnline = true);
    window.addEventListener('offline', () => this.isOnline = false);
  }

  ngOnDestroy(): void {
    window.removeEventListener('online', () => this.isOnline = true);
    window.removeEventListener('offline', () => this.isOnline = false);
  }

  setActiveTab(tab: 'dashboard' | 'students' | 'materials'): void {
    this.activeTab = tab;
  }

  toggleDarkMode(): void {
    this.isDarkMode = !this.isDarkMode;
    localStorage.setItem('theme', this.isDarkMode ? 'dark' : 'light');
  }
}


<div class="app-container" [class.dark-mode]="isDarkMode">
  <header class="app-header">
    <div class="header-content">
      <h1>{{ title }}</h1>
      <div class="header-controls">
        <button class="theme-toggle" (click)="toggleDarkMode()" [title]="isDarkMode ? 'Açık Mod' : 'Koyu Mod'">
          <i class="theme-icon">{{ isDarkMode ? '☀️' : '🌙' }}</i>
        </button>
        <div class="status-indicator" [class.online]="isOnline" [class.offline]="!isOnline">
          <span class="status-dot"></span>
          <span class="status-text">{{ isOnline ? 'Çevrimiçi' : 'Çevrimdışı' }}</span>
        </div>
      </div>
    </div>
  </header>

  <nav class="tab-navigation">
    <button 
      class="tab-button" 
      [class.active]="activeTab === 'dashboard'"
      (click)="setActiveTab('dashboard')"
    >
      <i class="tab-icon">📈</i>
      Dashboard
    </button>
    <button 
      class="tab-button" 
      [class.active]="activeTab === 'students'"
      (click)="setActiveTab('students')"
    >
      <i class="tab-icon">👥</i>
      Öğrenciler
    </button>
    <button 
      class="tab-button" 
      [class.active]="activeTab === 'materials'"
      (click)="setActiveTab('materials')"
    >
      <i class="tab-icon">📊</i>
      Materyaller
    </button>
  </nav>

  <main class="app-main">
    <app-dashboard *ngIf="activeTab === 'dashboard'"></app-dashboard>
    <app-student-list *ngIf="activeTab === 'students'"></app-student-list>
    <app-material-list *ngIf="activeTab === 'materials'"></app-material-list>
  </main>

  <footer class="app-footer">
    <p>&copy; 2025 Yönetim Sistemi. Tüm hakları saklıdır.</p>
    <div class="footer-links">
      <a href="#" class="footer-link">Yardım</a>
      <a href="#" class="footer-link">Hakkında</a>
      <a href="#" class="footer-link">İletişim</a>
    </div>
  </footer>
</div>
