import { Component, OnInit, OnDestroy } from '@angular/core';
import { Material } from '../models/material.model';
import { MaterialService } from '../services/material.service';

@Component({
  selector: 'app-material-list',
  templateUrl: './material-list.component.html',
  styleUrls: ['./material-list.component.css']
})
export class MaterialListComponent implements OnInit, OnDestroy {
  materials: Material[] = [];
  filteredMaterials: Material[] = [];
  isLoading = false;
  error: string | null = null;
  searchHisseKodu = '';
  lastRefreshTime = new Date();
  countdown = 30;
  private countdownInterval: any;

  // 🔽 Filtre değişkenleri eklendi
  filters = {
    id: '',
    tarih: '',
    hisseKodu: '',
    islem: '',
    adet: '',
    fiyat: '',
    toplam: '',
    komisyon: '',
    netTutar: '',
    anlikFiyat: '',
    anlikTutar: '',
    karZarar: '',
    karZararYuzde: '',
    ortalamaMaliyet: '',
    aciklama: ''
  };

  // 🔽 Sıralama için eklenen değişkenler
  sortColumn: string = '';
  sortDirection: 'asc' | 'desc' = 'asc';

  constructor(private materialService: MaterialService) {}

  ngOnInit(): void {
    this.loadMaterials();
    this.startCountdown();
  }

  ngOnDestroy(): void {
    if (this.countdownInterval) {
      clearInterval(this.countdownInterval);
    }
  }

  loadMaterials(): void {
    this.isLoading = true;
    this.error = null;

    this.materialService.getMaterials().subscribe({
      next: (data) => {
        this.materials = data;
        this.filteredMaterials = data;
        this.lastRefreshTime = new Date();
        this.isLoading = false;
      },
      error: (error) => {
        this.error = 'Materyal verileri yüklenirken hata oluştu: ' + error.message;
        this.isLoading = false;
      }
    });
  }

  onSearch(): void {
    if (this.searchHisseKodu.trim()) {
      this.isLoading = true;
      this.error = null;

      this.materialService.getMaterialByCode(this.searchHisseKodu.trim()).subscribe({
        next: (data) => {
          this.filteredMaterials = data;
          this.isLoading = false;
        },
        error: (error) => {
          this.error = 'Arama sırasında hata oluştu: ' + error.message;
          this.isLoading = false;
        }
      });
    } else {
      this.filteredMaterials = this.materials;
    }
  }

  clearSearch(): void {
    this.searchHisseKodu = '';
    this.filteredMaterials = this.materials;
  }

  refreshData(): void {
    this.loadMaterials();
    this.countdown = 120;
  }

  private startCountdown(): void {
    this.countdownInterval = setInterval(() => {
      this.countdown--;
      if (this.countdown <= 0) {
        this.refreshData();
      }
    }, 1000);
  }

  // 🔽 Sıralama fonksiyonu eklendi
  sortData(column: string): void {
    if (this.sortColumn === column) {
      // Aynı kolona tıklanırsa yönü değiştir
      this.sortDirection = this.sortDirection === 'asc' ? 'desc' : 'asc';
    } else {
      this.sortColumn = column;
      this.sortDirection = 'asc';
    }

    this.filteredMaterials.sort((a: any, b: any) => {
      const valueA = a[column];
      const valueB = b[column];

      // 📅 Tarih kolonu için özel sıralama
      if (column === 'tarih') {
        return this.compare(new Date(valueA), new Date(valueB));
      }

      // 🔢 Sayısal kolonlar
      if (typeof valueA === 'number' && typeof valueB === 'number') {
        return this.compare(valueA, valueB);
      }

      // 🔤 String kolonlar
      return this.compare(String(valueA), String(valueB));
    });
  }

  private compare(a: any, b: any): number {
    if (a < b) return this.sortDirection === 'asc' ? -1 : 1;
    if (a > b) return this.sortDirection === 'asc' ? 1 : -1;
    return 0;
  }

  // 🔽 Delete fonksiyonu eklendi
  deleteMaterial(material: Material): void {
    if (confirm(`ID: ${material.id} - "${material.hisseKodu}" hisse kodlu materyalı silmek istediğinizden emin misiniz?`)) {
      this.isLoading = true;
      this.error = null;

      this.materialService.deleteMaterial(material.id).subscribe({
        next: () => {
          // Silme işlemi başarılı, listeyi yenile
          this.loadMaterials();
          this.isLoading = false;
        },
        error: (error) => {
          this.error = 'Materyal silinirken hata oluştu: ' + error.message;
          this.isLoading = false;
        }
      });
    }
  }

  // 🔽 Güncelleme fonksiyonu eklendi
  updateMaterial(material: Material): void {
    // Placeholder: Gerçek uygulamada modal veya sayfa yönlendirmesi olacak
    const updateLink = `https://localhost:5001/api/material/update/${material.id}`;
    alert(`Güncelleme sayfasına yönlendirilecek:\n${updateLink}\n\nAPI henüz hazır değil.`);
  }

  // 🔽 Yeni kayıt fonksiyonu eklendi
  addNewMaterial(): void {
    // Placeholder: Gerçek uygulamada modal veya sayfa yönlendirmesi olacak
    const createLink = 'https://localhost:5001/api/material/create';
    alert(`Yeni kayıt sayfasına yönlendirilecek:\n${createLink}\n\nAPI henüz hazır değil.`);
  }

  // 🔽 Filtreleme fonksiyonları eklendi
  applyFilters(): void {
    this.filteredMaterials = this.materials.filter(material => {
      return this.matchesFilter(material.id.toString(), this.filters.id) &&
             this.matchesFilter(material.tarih, this.filters.tarih) &&
             this.matchesFilter(material.hisseKodu, this.filters.hisseKodu) &&
             this.matchesFilter(material.islem, this.filters.islem) &&
             this.matchesFilter(material.adet.toString(), this.filters.adet) &&
             this.matchesFilter(material.fiyat.toString(), this.filters.fiyat) &&
             this.matchesFilter(material.toplam.toString(), this.filters.toplam) &&
             this.matchesFilter(material.komisyon.toString(), this.filters.komisyon) &&
             this.matchesFilter(material.netTutar.toString(), this.filters.netTutar) &&
             this.matchesFilter(material.anlikFiyat.toString(), this.filters.anlikFiyat) &&
             this.matchesFilter(material.anlikTutar.toString(), this.filters.anlikTutar) &&
             this.matchesFilter(material.karZarar.toString(), this.filters.karZarar) &&
             this.matchesFilter(material.karZararYuzde.toString(), this.filters.karZararYuzde) &&
             this.matchesFilter(material.ortalamaMaliyet?.toString() || '', this.filters.ortalamaMaliyet) &&
             this.matchesFilter(material.aciklama || '', this.filters.aciklama);
    });
  }

  private matchesFilter(value: string, filter: string): boolean {
    if (!filter.trim()) {
      return true;
    }
    return value.toLowerCase().includes(filter.toLowerCase());
  }

  clearAllFilters(): void {
    this.filters = {
      id: '',
      tarih: '',
      hisseKodu: '',
      islem: '',
      adet: '',
      fiyat: '',
      toplam: '',
      komisyon: '',
      netTutar: '',
      anlikFiyat: '',
      anlikTutar: '',
      karZarar: '',
      karZararYuzde: '',
      ortalamaMaliyet: '',
      aciklama: ''
    };
    this.filteredMaterials = this.materials;
  }

  // 🔽 Toplam hesaplama fonksiyonları eklendi
  getTotalAdet(): number {
    return this.filteredMaterials.reduce((sum, material) => sum + material.adet, 0);
  }

  getTotalToplam(): number {
    return this.filteredMaterials.reduce((sum, material) => sum + material.toplam, 0);
  }

  getTotalKomisyon(): number {
    return this.filteredMaterials.reduce((sum, material) => sum + material.komisyon, 0);
  }

  getTotalNetTutar(): number {
    return this.filteredMaterials.reduce((sum, material) => sum + material.netTutar, 0);
  }

  getTotalAnlikTutar(): number {
    return this.filteredMaterials.reduce((sum, material) => sum + material.anlikTutar, 0);
  }

  getTotalKarZarar(): number {
    return this.filteredMaterials.reduce((sum, material) => sum + material.karZarar, 0);
  }

  // 🔽 Doğru hesaplama: (Anlık Tutar - Toplam) / Toplam * 100
  getWeightedKarZararYuzde(): number {
    if (this.filteredMaterials.length === 0) return 0;
    const totalToplam = this.getTotalToplam();
    const totalAnlikTutar = this.getTotalAnlikTutar();
    if (totalToplam === 0) return 0;
    return ((totalAnlikTutar - totalToplam) / totalToplam) * 100;
  }

  // 🔽 Doğru hesaplama: Toplam net tutar / Toplam adet
  getWeightedOrtalamaMaliyet(): number {
    if (this.filteredMaterials.length === 0) return 0;
    const totalNetTutar = this.getTotalNetTutar();
    const totalAdet = this.getTotalAdet();
    if (totalAdet === 0) return 0;
    return totalNetTutar / totalAdet;
  }
}
<div class="container">
  <div class="header">
    <h1>Materyal Listesi</h1>
    <div class="search-section">
      <div class="search-box">
        <input 
          type="text" 
          [(ngModel)]="searchHisseKodu" 
          placeholder="Hisse kodu girin..."
          class="search-input"
          (keyup.enter)="onSearch()"
        >
        <button (click)="onSearch()" class="search-btn">
          <i class="search-icon">🔍</i>
          Ara
        </button>
        <button (click)="clearSearch()" class="clear-btn">
          Temizle
        </button>
        <button (click)="refreshData()" class="refresh-btn">
          <i class="refresh-icon">🔄</i>
          Yenile
        </button>
        <button (click)="addNewMaterial()" class="add-btn">
          <i class="add-icon">➕</i>
          Yeni Kayıt
        </button>
      </div>
    </div>
  </div>

  <!-- Otomatik Yenileme Bilgisi -->
  <div class="refresh-info">
    <div class="refresh-status">
      <span class="refresh-label">Son Yenileme:</span>
      <span class="refresh-time">{{ lastRefreshTime | date:'HH:mm:ss' }}</span>
    </div>
    <div class="countdown-section">
      <span class="countdown-label">Sonraki Yenileme:</span>
      <span class="countdown-timer" [class.warning]="countdown <= 10">
        {{ countdown }}s
      </span>
    </div>
  </div>

  <div class="error-message" *ngIf="error">
    {{ error }}
  </div>

  <div class="loading" *ngIf="isLoading">
    <div class="spinner"></div>
    <p>Materyal verileri yükleniyor...</p>
  </div>

  <!-- 📊 Tablo Alanı -->
  <div class="table-container" *ngIf="!isLoading">
    <table class="material-table">
      <thead>
        <!-- Filtre satırı -->
        <tr class="filter-row">
          <th>
            <input 
              type="text" 
              [(ngModel)]="filters.id" 
              (input)="applyFilters()"
              placeholder="ID filtrele..."
              class="filter-input"
            >
          </th>
          <th>
            <input 
              type="text" 
              [(ngModel)]="filters.tarih" 
              (input)="applyFilters()"
              placeholder="Tarih filtrele..."
              class="filter-input"
            >
          </th>
          <th>
            <input 
              type="text" 
              [(ngModel)]="filters.hisseKodu" 
              (input)="applyFilters()"
              placeholder="Hisse kodu filtrele..."
              class="filter-input"
            >
          </th>
          <th>
            <input 
              type="text" 
              [(ngModel)]="filters.islem" 
              (input)="applyFilters()"
              placeholder="İşlem filtrele..."
              class="filter-input"
            >
          </th>
          <th>
            <input 
              type="text" 
              [(ngModel)]="filters.adet" 
              (input)="applyFilters()"
              placeholder="Adet filtrele..."
              class="filter-input"
            >
          </th>
          <th>
            <input 
              type="text" 
              [(ngModel)]="filters.fiyat" 
              (input)="applyFilters()"
              placeholder="Fiyat filtrele..."
              class="filter-input"
            >
          </th>
          <th>
            <input 
              type="text" 
              [(ngModel)]="filters.toplam" 
              (input)="applyFilters()"
              placeholder="Toplam filtrele..."
              class="filter-input"
            >
          </th>
          <th>
            <input 
              type="text" 
              [(ngModel)]="filters.komisyon" 
              (input)="applyFilters()"
              placeholder="Komisyon filtrele..."
              class="filter-input"
            >
          </th>
          <th>
            <input 
              type="text" 
              [(ngModel)]="filters.netTutar" 
              (input)="applyFilters()"
              placeholder="Net tutar filtrele..."
              class="filter-input"
            >
          </th>
          <th>
            <input 
              type="text" 
              [(ngModel)]="filters.anlikFiyat" 
              (input)="applyFilters()"
              placeholder="Anlık fiyat filtrele..."
              class="filter-input"
            >
          </th>
          <th>
            <input 
              type="text" 
              [(ngModel)]="filters.anlikTutar" 
              (input)="applyFilters()"
              placeholder="Anlık tutar filtrele..."
              class="filter-input"
            >
          </th>
          <th>
            <input 
              type="text" 
              [(ngModel)]="filters.karZarar" 
              (input)="applyFilters()"
              placeholder="Kar/zarar filtrele..."
              class="filter-input"
            >
          </th>
          <th>
            <input 
              type="text" 
              [(ngModel)]="filters.karZararYuzde" 
              (input)="applyFilters()"
              placeholder="Kar/zarar % filtrele..."
              class="filter-input"
            >
          </th>
          <th>
            <input 
              type="text" 
              [(ngModel)]="filters.ortalamaMaliyet" 
              (input)="applyFilters()"
              placeholder="Ortalama maliyet filtrele..."
              class="filter-input"
            >
          </th>
          <th>
            <input 
              type="text" 
              [(ngModel)]="filters.aciklama" 
              (input)="applyFilters()"
              placeholder="Açıklama filtrele..."
              class="filter-input"
            >
          </th>
          <th>
            <button (click)="clearAllFilters()" class="clear-filters-btn">
              🗑️ Temizle
            </button>
          </th>
        </tr>
        
        <!-- Header satırı -->
        <tr>
          <th (click)="sortData('id')" title="ID'ye göre sırala">
            ID
            <span *ngIf="sortColumn === 'id'" class="sort-indicator">
              {{ sortDirection === 'asc' ? '▲' : '▼' }}
            </span>
          </th>
          <th (click)="sortData('tarih')" title="Tarihe göre sırala">
            Tarih
            <span *ngIf="sortColumn === 'tarih'" class="sort-indicator">
              {{ sortDirection === 'asc' ? '▲' : '▼' }}
            </span>
          </th>
          <th (click)="sortData('hisseKodu')" title="Hisse koduna göre sırala">
            Hisse Kodu
            <span *ngIf="sortColumn === 'hisseKodu'" class="sort-indicator">
              {{ sortDirection === 'asc' ? '▲' : '▼' }}
            </span>
          </th>
          <th (click)="sortData('islem')" title="İşleme göre sırala">
            İşlem
            <span *ngIf="sortColumn === 'islem'" class="sort-indicator">
              {{ sortDirection === 'asc' ? '▲' : '▼' }}
            </span>
          </th>
          <th (click)="sortData('adet')" title="Adete göre sırala">
            Adet
            <span *ngIf="sortColumn === 'adet'" class="sort-indicator">
              {{ sortDirection === 'asc' ? '▲' : '▼' }}
            </span>
          </th>
          <th (click)="sortData('fiyat')" title="Fiyata göre sırala">
            Fiyat
            <span *ngIf="sortColumn === 'fiyat'" class="sort-indicator">
              {{ sortDirection === 'asc' ? '▲' : '▼' }}
            </span>
          </th>
          <th (click)="sortData('toplam')" title="Toplama göre sırala">
            Toplam
            <span *ngIf="sortColumn === 'toplam'" class="sort-indicator">
              {{ sortDirection === 'asc' ? '▲' : '▼' }}
            </span>
          </th>
          <th (click)="sortData('komisyon')" title="Komisyona göre sırala">
            Komisyon
            <span *ngIf="sortColumn === 'komisyon'" class="sort-indicator">
              {{ sortDirection === 'asc' ? '▲' : '▼' }}
            </span>
          </th>
          <th (click)="sortData('netTutar')" title="Net tutara göre sırala">
            Net Tutar
            <span *ngIf="sortColumn === 'netTutar'" class="sort-indicator">
              {{ sortDirection === 'asc' ? '▲' : '▼' }}
            </span>
          </th>
          <th (click)="sortData('anlikFiyat')" title="Anlık fiyata göre sırala">
            Anlık Fiyat
            <span *ngIf="sortColumn === 'anlikFiyat'" class="sort-indicator">
              {{ sortDirection === 'asc' ? '▲' : '▼' }}
            </span>
          </th>
          <th (click)="sortData('anlikTutar')" title="Anlık tutara göre sırala">
            Anlık Tutar
            <span *ngIf="sortColumn === 'anlikTutar'" class="sort-indicator">
              {{ sortDirection === 'asc' ? '▲' : '▼' }}
            </span>
          </th>
          <th (click)="sortData('karZarar')" title="Kar/zarara göre sırala">
            Kar/Zarar
            <span *ngIf="sortColumn === 'karZarar'" class="sort-indicator">
              {{ sortDirection === 'asc' ? '▲' : '▼' }}
            </span>
          </th>
          <th (click)="sortData('karZararYuzde')" title="Kar/zarar yüzdesine göre sırala">
            Kar/Zarar %
            <span *ngIf="sortColumn === 'karZararYuzde'" class="sort-indicator">
              {{ sortDirection === 'asc' ? '▲' : '▼' }}
            </span>
          </th>
          <th (click)="sortData('ortalamaMaliyet')" title="Ortalama maliyete göre sırala">
            Ortalama Maliyet
            <span *ngIf="sortColumn === 'ortalamaMaliyet'" class="sort-indicator">
              {{ sortDirection === 'asc' ? '▲' : '▼' }}
            </span>
          </th>
          <th (click)="sortData('aciklama')" title="Açıklamaya göre sırala">
            Açıklama
            <span *ngIf="sortColumn === 'aciklama'" class="sort-indicator">
              {{ sortDirection === 'asc' ? '▲' : '▼' }}
            </span>
          </th>
          <th>İşlemler</th>
        </tr>
      </thead>

      <tbody>
        <tr *ngFor="let material of filteredMaterials" class="material-row">
          <td>{{ material.id }}</td>
          <td>{{ material.tarih | date:'dd.MM.yyyy' }}</td>
          <td>{{ material.hisseKodu }}</td>
          <td>{{ material.islem }}</td>
          <td>{{ material.adet | number:'1.2-2' }}</td>
          <td>{{ material.fiyat | number:'1.2-2' }}</td>
          <td>{{ material.toplam | number:'1.2-2' }}</td>
          <td>{{ material.komisyon | number:'1.2-2' }}</td>
          <td>{{ material.netTutar | number:'1.2-2' }}</td>
          <td>{{ material.anlikFiyat | number:'1.2-2' }}</td>
          <td>{{ material.anlikTutar | number:'1.2-2' }}</td>
          <td [class]="material.karZarar >= 0 ? 'profit' : 'loss'">
            {{ material.karZarar | number:'1.2-2' }}
          </td>
          <td [class]="material.karZararYuzde >= 0 ? 'profit' : 'loss'">
            {{ material.karZararYuzde | number:'1.2-2' }}%
          </td>
          <td>{{ material.ortalamaMaliyet | number:'1.2-2' }}</td>
          <td>{{ material.aciklama }}</td>
          <td>
            <button 
              (click)="updateMaterial(material)" 
              class="update-btn"
              title="Materyalı Güncelle"
            >
              ✏️ Güncelle
            </button>
            <button 
              (click)="deleteMaterial(material)" 
              class="delete-btn"
              title="Materyalı Sil"
            >
              🗑️ Sil
            </button>
          </td>
        </tr>
      </tbody>
      
      <!-- Toplam satırı -->
      <tfoot>
        <tr class="total-row">
          <td class="total-label">TOPLAM</td>
          <td>-</td>
          <td>-</td>
          <td>-</td>
          <td class="total-value">{{ getTotalAdet() | number:'1.2-2' }}</td>
          <td>-</td>
          <td class="total-value">{{ getTotalToplam() | number:'1.2-2' }}</td>
          <td class="total-value">{{ getTotalKomisyon() | number:'1.2-2' }}</td>
          <td class="total-value">{{ getTotalNetTutar() | number:'1.2-2' }}</td>
          <td>-</td>
          <td class="total-value">{{ getTotalAnlikTutar() | number:'1.2-2' }}</td>
          <td class="total-value" [class]="getTotalKarZarar() >= 0 ? 'profit' : 'loss'">
            {{ getTotalKarZarar() | number:'1.2-2' }}
          </td>
          <td class="total-value" [class]="getWeightedKarZararYuzde() >= 0 ? 'profit' : 'loss'">
            {{ getWeightedKarZararYuzde() | number:'1.2-2' }}%
          </td>
          <td class="total-value">{{ getWeightedOrtalamaMaliyet() | number:'1.2-2' }}</td>
          <td>-</td>
          <td>-</td>
        </tr>
      </tfoot>
    </table>

    <div class="no-data" *ngIf="filteredMaterials.length === 0 && !error">
      <p>Gösterilecek materyal bulunamadı.</p>
    </div>
  </div>

  <div class="info-section">
    <p class="total-count">
      Toplam {{ filteredMaterials.length }} materyal gösteriliyor
    </p>
  </div>
</div>
